import { TaskActionType } from '../actions/task.action';
// create a dummy initial state
const initialState = [{
  id: 1,
  title: 'Test task'
}];
export function taskReducer(state = initialState, action) {
  switch (action.type) {
    case TaskActionType.ADD_ITEM:
      return [...state, action.payload];
    default:
      return state;
  }
}
